import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import logo from './logo.svg';
import './App.css';
import { fetchUserTransactions } from './actions';
import Container from '@material-ui/core/Container';
import Scroll from './components/Scroll';
import ListUI from './components/ListUI';
import TableUI from './components/TableUI';
import EditableTableUI from './components/EditableTableUI';
import CardUI from './components/CardUI';


function App({fetchUserTransactions, transactions}) {

  // const [transactions, setTransactions] = useState([])
  const [parameter, setParameter] = useState('');
  const [total, setTotal] = useState(0);
  const [reloadTrick, setReloadTrick] = useState("");

  const demoTransactions = React.createContext();

  console.log('RELOAD TRICK ', reloadTrick)
  useEffect(() => {
    console.log('Use Effect ', transactions)

      // fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/transactions?category=' + parameter)
      // .then(response => response.json())
      // .then(data => { setTransactions(data) });

      fetchUserTransactions();

    // console.log(count)
  }, [reloadTrick]) // if you add count, only run if count changes.




  const onSelect = ({value}) => {
    onFilter(value)
    console.log('on search', value)
    // setParameter(value)
    if(value === 'all'){
      let calcul = 0;
      transactions.forEach(tr => {
      calcul += tr.transaction_details.total
      })
      console.log('ALL ALL ALL', calcul);
      setTotal(Number(calcul).toFixed(1));
    }
    else{
      let filteredData = transactions.filter( tr => tr.transaction_details.category === value);
      let calcul = 0;
      filteredData.forEach(tr => {
        calcul += tr.transaction_details.total
      })
      setTotal(Number(calcul).toFixed(1));
      console.log('TOTAL ', total)
    }
  }

  const onFilter = (value) => {
    console.log('ON FILTER');
    setParameter(value);
  }

  const makeid = (length) => {
    var result           = '';
    var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    var charactersLength = characters.length;
    for ( var i = 0; i < length; i++ ) {
      result += characters.charAt(Math.floor(Math.random() * 
      charactersLength));
   }
   return result;
}

  const reload = () => {
    console.log('reload')
    setReloadTrick(makeid(5))
  }

  const filteredTransactions = transactions.filter(transaction =>{
    console.log('transactions ', transactions)
    if(parameter === 'all'){
      return transactions
    }
    console.log('transaction tr tr ', transaction.transaction_details.category)
    return transaction.transaction_details.category.includes(parameter);
  })

  return !transactions.length ?
    <h1>Loading</h1> :
    (
      <div className="root">
        <div className="header">
         <h1>TRANSACTIONS</h1>
        </div>
        {/* <Scroll>
          <ListUI data={transactions} />
        </Scroll> */}
        {/* <TableUI onSubmitTag={(value) => onSubmitTag(value)} data={filteredTransactions}/> */}
        <ListUI reload={() => reload()} data={filteredTransactions}/>
        <div className="header">
         <h1>ANALYTICS</h1>
        </div>
        <CardUI onSelect={onSelect} title={"Total"} >
          <h1>{total}  â‚ª</h1>
        </CardUI>
      </div>
    )
}

const mapStateToProps = (state) => {
  return {
    transactions: state.transactions.tr
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUserTransactions: () => dispatch(fetchUserTransactions())
  }
}

export default connect(mapStateToProps, mapDispatchToProps) (App);
