import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchUserTransactions } from '../actions';
import { List, message, Avatar, Spin, Modal, Select, Button } from 'antd';
import CreatableSelect from 'react-select/creatable';
import InfiniteScroll from 'react-infinite-scroller';
import EditableForm from './EditableForm';
import PopoverUI from './PopoverUI';
import ModalUI from './ModalUI';
import './list.css'

const { Option } = Select;

const fakeDataUrl = 'https://randomuser.me/api/?results=5&inc=name,gender,email,nat&noinfo';

const ListUI = ({ fetchUserTransactions, transactions,  reload }) => {

  const [visible, setVisible] = useState(false);
  const [loading, setLoading] = useState(false);
  const [disabled, setDisabled] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [category, setCategory] = useState("");
  const [reloadTrick, setReloadTrick] = useState("");
  const [categories, setCategories] = useState([])
  const [create, setCreate] = useState(false)
  const [newCategory, setNewCategory] = useState(null)
  const [value, setValue] = useState(selectedItem && selectedItem.transaction_details.category)



  useEffect(() => {
    // console.log('use effect select ', value)
    // fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories')
    //   .then(response => response.json())
    //   .then(data => { setCategories(data) });

    // console.log(count)

    fetchUserTransactions()
  }, [value]) // if you add count, only run if count changes.

  let selectorOptions = categories.map( c => {
    return {
      label:c.name,
      value:c.name
    }
  })

  console.log('data data ', selectorOptions)

  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setVisible(false);
    console.log('resect select')
  };

  const handleCancel = () => {
    setVisible(false);
  };

  const onTransactionClick = (item) => {
    console.log('ITEM ITEM ', item)
    setSelectedItem(item);
    setValue(item.transaction_details.category)
    showModal()
  }

  const onFinish = (value, item) => {
    handleOk()
    onSubmitTag(value, item)
  };

  const reloadModal = () => {
    console.log('RELOAD MODAL LIST UI')
    handleOk()
  };

  const isCreate = (value) => {
    setCreate(value)
  }

  const onType = (value) => {
    setNewCategory(value)
  }





  function onBlur() {
    console.log('blur')
    reload()
  }

  function onFocus() {
    console.log('focus')
  }


  function onSearch(val) {
    const filteredCategories = categories.filter(category => {
      return category.name.includes(val);
    })
    if (filteredCategories.length > 0) {


    }
    else {
      // setOnCreate(true)
      isCreate(true)
      onType(val)
    }
  }

  function filterOption(input, option) {
    const result = option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    return result
  }

  const onCreate = (v) => {
    setLoading(true);
    setDisabled(true);
    if (v != null) {
      fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories`, {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ "category": v })
      })
        .then(response => response.json())
        .then(data => { 
          setLoading(false);
          setDisabled(false);
          setCategories(data) 
        });
    }

  }
  const onSubmitTag = (value) => {
    setValue(value.value)
    console.log('VALUE VALUE ', value)
    console.log('SELECTED ITEM ', selectedItem)
    fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/transactions/${selectedItem.transaction_details.uuid}`, {
      method: 'PUT',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ "category": value.value })
    })
      .then(response => response.json())
      .then(data => {
        reload()
        handleOk()
        setValue('')
        // setVisible(false)
        // reloadModal(value);

      });
  }

  return (
    <div className="demo-infinite-container">
      <InfiniteScroll
        initialLoad={false}
        pageStart={0}
        useWindow={false}
        loadMore={() => console.log('load more')}
      >
        <ModalUI/>
        <List
          dataSource={transactions}
          renderItem={item => (
            <div data-tag="test" onClick={() => onTransactionClick(item)}>
              <List.Item data={item} key={item.transaction_details.id}>
                <List.Item.Meta

                  title={<a><h2 style={{ fontWeight:"bold"}}>{item.transaction_details.vendor}</h2></a>}

                />
                <List.Item.Meta
                  title={<a><h2 style={{fontWeight:"bold"}}>{item.transaction_details.category}</h2></a>}
                  description={item.transaction_details.tag}
                />

                <h2 style={{fontWeight:"bold"}}>{item.transaction_details.total} â‚ª</h2>


              </List.Item>
            </div>
          )}
        >
        </List>
      </InfiniteScroll>
    </div>
  );

}
const mapStateToProps = (state) => {
  return {
    transactions: state.transactions
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUserTransactions: () => dispatch(fetchUserTransactions())
  }
}
export default connect(mapStateToProps, mapDispatchToProps) (ListUI);
