import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import Container from '@material-ui/core/Container';
import Scroll from './components/Scroll';
import ListUI from './components/ListUI';
import TableUI from './components/TableUI';
import EditableTableUI from './components/EditableTableUI';
import CardUI from './components/CardUI';


function App() {

  const [transactions, setTransactions] = useState([])
  const [parameter, setParameter] = useState('');
  const [total, setTotal] = useState(0);
  const [reloadTrick, setReloadTrick] = useState("");

  const demoTransactions = React.createContext();

  useEffect(() => {
    console.log('PARAMETER ', parameter)

      fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/transactions?category=' + parameter)
      .then(response => response.json())
      .then(data => { setTransactions(data) });

    // console.log(count)
  }, []) // if you add count, only run if count changes.

  const onSubmitTag = (value, item) => {
    console.log('ON FINISH ON FINISH', value.transaction.tag)
    console.log('ON FINISH ON FINISH', item)
    fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/transactions/${item.transaction_details.uuid}`, {
      method: 'PUT',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body:JSON.stringify({"tag":value.transaction.tag})
    })
    .then(response => response.json())
    .then(data => { setTransactions(data) });
  }

  console.log('TRANSAC ', transactions);

  const onSelect = (value) => {
    onFilter(value)
    console.log('on search', value)
    // setParameter(value)
    if(value === 'all'){
      let calcul = 0;
      transactions.forEach(tr => {
      calcul += tr.transaction_details.total
      })
      console.log('ALL ALL ALL', calcul);
      setTotal(Number(calcul).toFixed(1));
    }
    else{
      let filteredData = transactions.filter( tr => tr.transaction_details.category === value);
      let calcul = 0;
      filteredData.forEach(tr => {
        calcul += tr.transaction_details.total
      })
      setTotal(Number(calcul).toFixed(1));
      console.log('TOTAL ', total)
    }
  }

  const onFilter = (value) => {
    console.log('ON FILTER');
    setParameter(value);
  }

  const reload = () => {
    console.log('reload')
    setReloadTrick(' lets reload')
  }

  const filteredTransactions = transactions.filter(transaction =>{
    if(parameter === 'all'){
      return transactions
    }
    return transaction.transaction_details.category.includes(parameter);
  })

  return !transactions.length ?
    <h1>Loading</h1> :
    (
      <div className="root">
        <div className="header">
         <h1>TRANSACTIONS</h1>
        </div>
        {/* <Scroll>
          <ListUI data={transactions} />
        </Scroll> */}
        {/* <TableUI onSubmitTag={(value) => onSubmitTag(value)} data={filteredTransactions}/> */}
        <ListUI reload={() => reload()} onSubmitTag={(value, item) => onSubmitTag(value, item)} data={filteredTransactions}/>
        <div className="header">
         <h1>ANALYTICS</h1>
        </div>
        <CardUI onSelect={onSelect} title={"Total"} >
          <h1>{total}  â‚ª</h1>
        </CardUI>
      </div>
    )
}

export default App;
